mapper.select:
select * from major m inner join department d on m.did = d.did
m.is_del=0 and d.is_del=0
and m.mname=#{mname}
and d.dname=#{dname}
order by m.mid desc

select *
from  grade g
inner join major m on g.mid = m.mid
inner join department d on m.did = d.did

    g.is_del=0 and m.is_del=0 and d.is_del=0

        and g.gname=#{gname}


        and m.mname=#{mname}


        and d.dname=#{dname}


order by g.gid desc

select *
from classinfo c
inner join grade g on c.gid = g.gid
inner join major m on g.mid = m.mid
inner join department d on m.did = d.did

        c.is_del=0 and g.is_del=0 and m.is_del=0 and d.is_del=0

            and c.classname=#{classname}


            and g.gname=#{gname}


            and m.mname=#{mname}


            and d.dname=#{dname}


order by c.classid desc
